document.addEventListener("DOMContentLoaded",()=>{let t=window.currentLocale,e={wrapper:document.querySelector("#language"),menu:document.querySelector("#language-show"),languageActive:document.querySelector(".language-active"),languageToggle:document.querySelector(".language-toggle"),ru:document.querySelector("#ru"),en:document.querySelector("#en"),zh:document.querySelector("#zh")};e.languageActive.id=t.toLowerCase(),e.languageActive.textContent=t,e.languageToggle.addEventListener("click",()=>{e.menu.classList.toggle("active")})});
